import { IconArrowLeft, IconArrowRight } from "@tabler/icons-react"
import { Button } from "@/registry/bases/radix/ui/button"
import { ButtonGroup } from "@/registry/bases/radix/ui/button-group"
import {
  InputGroup,
  InputGroupAddon,
  InputGroupInput,
} from "@/registry/bases/radix/ui/input-group"
import { Kbd, KbdGroup } from "@/registry/bases/radix/ui/kbd"
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/registry/bases/radix/ui/tooltip"
import { IconPlaceholder } from "@/app/(design)/design/components/icon-placeholder"

export default function KbdExample() {
  return (
    <div className="bg-background flex min-h-screen items-center justify-center p-6 lg:p-12">
      <div className="flex w-full max-w-lg flex-col gap-12">
        <KbdBasic />
        <KbdModifierKeys />
        <KbdGroupExample />
        <KbdArrowKeys />
        <KbdWithIcons />
        <KbdWithIconsAndText />
        <KbdInInputGroup />
        <KbdInTooltip />
        <KbdWithSamp />
      </div>
    </div>
  )
}

function KbdBasic() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="Basic"><div className="flex items-center gap-2">
              <Kbd>Ctrl</Kbd>
              <Kbd>⌘K</Kbd>
              <Kbd>Ctrl + B</Kbd>
            </div></div>
  )
}

function KbdModifierKeys() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="Modifier Keys"><div className="flex items-center gap-2">
              <Kbd>⌘</Kbd>
              <Kbd>C</Kbd>
            </div></div>
  )
}

function KbdGroupExample() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="KbdGroup"><KbdGroup>
              <Kbd>Ctrl</Kbd>
              <Kbd>Shift</Kbd>
              <Kbd>P</Kbd>
            </KbdGroup></div>
  )
}

function KbdArrowKeys() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="Arrow Keys"><div className="flex items-center gap-2">
              <Kbd>↑</Kbd>
              <Kbd>↓</Kbd>
              <Kbd>←</Kbd>
              <Kbd>→</Kbd>
            </div></div>
  )
}

function KbdWithIcons() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="With Icons"><KbdGroup>
              <Kbd>
                <IconPlaceholder
                  lucide="CircleDashedIcon"
                  tabler="IconCircleDashed"
                  hugeicons="DashedLineCircleIcon"
                />
              </Kbd>
              <Kbd>
                <IconArrowLeft />
              </Kbd>
              <Kbd>
                <IconArrowRight />
              </Kbd>
            </KbdGroup></div>
  )
}

function KbdWithIconsAndText() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="With Icons and Text"><KbdGroup>
              <Kbd>
                <IconArrowLeft />
                Left
              </Kbd>
              <Kbd>
                <IconPlaceholder
                  lucide="CircleDashedIcon"
                  tabler="IconCircleDashed"
                  hugeicons="DashedLineCircleIcon"
                />
                Voice Enabled
              </Kbd>
            </KbdGroup></div>
  )
}

function KbdInInputGroup() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="InputGroup"><InputGroup>
              <InputGroupInput />
              <InputGroupAddon>
                <Kbd>Space</Kbd>
              </InputGroupAddon>
            </InputGroup></div>
  )
}

function KbdInTooltip() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="Tooltip"><Tooltip>
              <TooltipTrigger asChild>
                <Button size="icon-sm" variant="outline">
                  <IconPlaceholder
                    lucide="SaveIcon"
                    tabler="IconDeviceFloppy"
                    hugeicons="FloppyDiskIcon"
                  />
                </Button>
              </TooltipTrigger>
              <TooltipContent>
                <div className="flex items-center gap-2">
                  Save Changes <Kbd>S</Kbd>
                </div>
              </TooltipContent>
            </Tooltip></div>
  )
}

function KbdWithSamp() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="With samp"><Kbd>
              <samp>File</samp>
            </Kbd></div>
  )
}
