import { Checkbox } from "@/registry/bases/radix/ui/checkbox"
import { Field } from "@/registry/bases/radix/ui/field"
import { Input } from "@/registry/bases/radix/ui/input"
import { Label } from "@/registry/bases/radix/ui/label"
import { Textarea } from "@/registry/bases/radix/ui/textarea"

export default function LabelExample() {
  return (
    <div className="bg-background flex min-h-screen items-center justify-center p-6 lg:p-12">
      <div className="flex w-full max-w-lg flex-col gap-12">
        <LabelWithInput />
        <LabelDisabled />
        <LabelWithCheckbox />
        <LabelWithTextarea />
      </div>
    </div>
  )
}

function LabelWithCheckbox() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="With Checkbox"><div className="flex items-center gap-3">
              <Checkbox id="label-demo-terms" />
              <Label htmlFor="label-demo-terms">Accept terms and conditions</Label>
            </div></div>
  )
}

function LabelWithInput() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="With Input"><Field>
              <Label htmlFor="label-demo-username">Username</Label>
              <Input id="label-demo-username" placeholder="Username" />
            </Field></div>
  )
}

function LabelDisabled() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="Disabled"><Field data-disabled={true}>
              <Label htmlFor="label-demo-disabled">Disabled</Label>
              <Input id="label-demo-disabled" placeholder="Disabled" disabled />
            </Field></div>
  )
}

function LabelWithTextarea() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="With Textarea"><Field>
              <Label htmlFor="label-demo-message">Message</Label>
              <Textarea id="label-demo-message" placeholder="Message" />
            </Field></div>
  )
}
