import {
  Field,
  FieldContent,
  FieldDescription,
  FieldLabel,
  FieldTitle,
} from "@/registry/bases/radix/ui/field"
import { Label } from "@/registry/bases/radix/ui/label"
import { Switch } from "@/registry/bases/radix/ui/switch"

export default function SwitchExample() {
  return (
    <div className="bg-background flex min-h-screen items-center justify-center p-6 lg:p-12">
      <div className="flex w-full max-w-lg flex-col gap-12">
        <SwitchBasic />
        <SwitchWithDescription />
        <SwitchDisabled />
      </div>
    </div>
  )
}

function SwitchBasic() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="Basic"><Field orientation="horizontal">
              <Switch id="switch-basic" />
              <FieldLabel htmlFor="switch-basic">Airplane Mode</FieldLabel>
            </Field></div>
  )
}

function SwitchWithLabel() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="With Label"><div className="flex items-center gap-2">
              <Switch id="switch-bluetooth" defaultChecked />
              <Label htmlFor="switch-bluetooth">Bluetooth</Label>
            </div></div>
  )
}

function SwitchWithDescription() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="With Description"><FieldLabel htmlFor="switch-focus-mode">
              <Field orientation="horizontal">
                <FieldContent>
                  <FieldTitle>Share across devices</FieldTitle>
                  <FieldDescription>
                    Focus is shared across devices, and turns off when you leave the
                    app.
                  </FieldDescription>
                </FieldContent>
                <Switch id="switch-focus-mode" />
              </Field>
            </FieldLabel></div>
  )
}

function SwitchDisabled() {
  return (
    <div className="bg-background border flex overflow-hidden rounded-xl p-8" title="Disabled"><div className="flex flex-col gap-12">
              <div className="flex items-center gap-2">
                <Switch id="switch-disabled-unchecked" disabled />
                <Label htmlFor="switch-disabled-unchecked">
                  Disabled (Unchecked)
                </Label>
              </div>
              <div className="flex items-center gap-2">
                <Switch id="switch-disabled-checked" defaultChecked disabled />
                <Label htmlFor="switch-disabled-checked">Disabled (Checked)</Label>
              </div>
            </div></div>
  )
}
