import type { JSX, ReactNode } from "react"

interface UserCardProps {
  title: string
  children: ReactNode
  icon: JSX.Element
}

export default function UserCard({
  children,
  icon,
  title,
}: Readonly<UserCardProps>): JSX.Element {
  return (
    <article className="flex h-fit w-full flex-col gap-4 rounded-lg border border-border bg-background p-4 leading-tight">
      <div className="flex items-center justify-between">
        <h2 className="text-sm font-medium text-foreground">{title}</h2>
        {icon}
      </div>
      {children}
    </article>
  )
}

import { Star, StarHalf } from "lucide-react"

export const fillStars = (rating: number): JSX.Element[] => {
  const stars = Array.from({ length: 5 }, (_, index) => {
    if (index < Math.floor(rating)) {
      return (
        <Star
          key={index}
          size={16}
          className="fill-foreground"
          strokeWidth={0}
        />
      )
    } else if (index < rating && rating - index >= 0.5) {
      return (
        <span key={index} className="relative">
          <StarHalf size={16} className="fill-foreground" strokeWidth={0} />
          <Star
            size={16}
            strokeWidth={0.5}
            fill="transparent"
            className="absolute top-0"
          />
        </span>
      )
    } else {
      return (
        <Star
          key={index}
          size={16}
          strokeWidth={1}
          fill="transparent"
          className="text-foreground/30"
        />
      )
    }
  })

  return stars
}
