import * as React from "react"

import { SearchForm } from "@/registry/default/blocks/sidebar-01/components/search-form"
import { VersionSwitcher } from "@/registry/default/blocks/sidebar-01/components/version-switcher"
import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarHeader,
  SidebarMenu,
