import * as React from "react"

import { cn } from "@/lib/utils"
import { Button } from "@/registry/new-york-v4/ui/button"

function ButtonGroup({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="button-group"
      className={cn(
        "inline-flex h-9 items-center justify-center rounded-md p-1 text-muted-foreground",
        className
      )}
      {...props}
    />
  )
}

function ButtonGroupItem({
  className,
  ...props
}: React.ComponentProps<typeof Button>) {
  return (
    <Button
      className={cn(
        "h-7 rounded-sm px-2 shadow-none hover:bg-accent hover:text-accent-foreground focus-visible:ring-0 focus-visible:ring-offset-0 data-[state=active]:bg-background data-[state=active]:text-foreground data-[state=active]:shadow-sm",
        className
      )}
      variant="ghost"
      size="sm"
      {...props}
    />
  )
}

export { ButtonGroup, ButtonGroupItem }
