import type { JSX } from "react"

import { cn } from "@/lib/utils"

interface OrderStateProps {
  states: {
    status: string
    icon: JSX.Element
    description: string
    isActive: boolean
  }[]
}

export default function OrderState({
  states,
}: Readonly<OrderStateProps>): JSX.Element {
  return (
    <ul className="grid w-full grid-cols-1 gap-4 md:grid-cols-3 lg:grid-cols-4 2xl:grid-cols-5">
      {states.map((state) => (
        <li
          key={state.status}
          className={cn(
            "flex items-center gap-2 rounded-lg border p-1",
            state.isActive
              ? "bg-primary text-primary-foreground"
              : "border-border"
          )}
        >
          <span
            className={cn(
              "rounded-sm p-2",
              state.isActive
                ? "bg-primary-foreground text-primary"
                : "bg-foreground text-primary-foreground"
            )}
          >
            {state.icon}
          </span>
          <div className="">
            <p className="text-sm font-medium">{state.status}</p>
            <p
              className={cn(
                "text-xs",
                state.isActive
                  ? "text-primary-foreground/80"
                  : "text-foreground/80"
              )}
            >
              {state.description}
            </p>
          </div>
        </li>
      ))}
    </ul>
  )
}

import { useMemo, type JSX } from "react"

import {
  Card,
  CardContent
} from "@/registry/new-york/ui/card"
import { Separator } from "@/registry/new-york/ui/separator"

interface OrderItem {
  name: string
  color: string
  size: string
  price: number
  quantity: number
}

interface OrderSummaryProps {
  items: OrderItem[]
  shippingCost: number
  shippingDiscount: number
}

export default function OrderSummary({
  items,
  shippingCost,
  shippingDiscount,
}: OrderSummaryProps): JSX.Element {

  const total = useMemo(() => {
    const subtotal = items.reduce((acc, item) => acc + item.price * item.quantity, 0)
    return subtotal + shippingCost - shippingDiscount
  }, [items, shippingCost, shippingDiscount])


  const subtotal = useMemo(() => {
    return items.reduce((acc, item) => acc + item.price * item.quantity, 0)
  }, [items])


  return (
    <Card className="w-full rounded-md pt-6 shadow-none">
      <CardContent className="space-y-4">
        <div className="space-y-2">
          {items.map((item, index) => (
            <div key={index} className="flex justify-between text-sm">
              <div>
                <p className="font-medium">{item.name}</p>
                <p className="text-muted-foreground">
                  {item.color} • Size {item.size} • Qty {item.quantity}
                </p>
              </div>
              <p className="font-medium">RP {item.price.toLocaleString()}</p>
            </div>
          ))}
        </div>

        <Separator />

        <div className="space-y-2 text-sm">
          <div className="flex justify-between">
            <p>Product Price ({items.length} Item)</p>
            <p>RP {subtotal.toLocaleString()}</p>
          </div>
          <div className="flex justify-between">
            <p>Shipping Cost Subtotal</p>
            <p>RP {shippingCost.toLocaleString()}</p>
          </div>
          <div className="flex justify-between text-green-600">
            <p>Shipping Discount</p>
            <p>-RP {shippingDiscount.toLocaleString()}</p>
          </div>
        </div>

        <Separator />

        <div className="flex justify-between font-medium">
          <p>Total Sales</p>
          <p>RP {total.toLocaleString()}</p>
        </div>
      </CardContent>
    </Card>
  )
}

import type { JSX } from "react"
import { CheckCircle2, Circle } from "lucide-react"

interface OrderTrackingProps {
  steps: {
    name: string
    timestamp: string
    isCompleted: boolean
  }[]
}

export default function OrderTracking({
  steps = [],
}: Readonly<OrderTrackingProps>): JSX.Element {
  return (
    <>
      <h3 className="mb-4 text-lg font-semibold">Order Tracking</h3>
      {steps.length > 0 ? (
        <div className="w-full max-w-md">
          <div className="">
            {steps.map((step, index) => (
              <div key={index} className="flex">
                <div className="flex flex-col items-center">
                  {step.isCompleted ? (
                    <CheckCircle2 className="h-6 w-6 shrink-0 text-primary/70" />
                  ) : (
                    <Circle className="h-6 w-6 shrink-0 text-muted-foreground" />
                  )}
                  {index < steps.length - 1 && (
                    <div
                      className={`w-[1.5px] grow ${
                        steps[index + 1].isCompleted
                          ? "bg-primary/70"
                          : "bg-muted-foreground"
                      }`}
                    />
                  )}
                </div>
                <div className="ml-3 pb-6">
                  <p className="text-sm font-medium">{step.name}</p>
                  <p className="text-sm text-muted-foreground">
                    {step.timestamp}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      ) : (
        <p className="text-sm text-foreground/80">
          This order has no tracking information.
        </p>
      )}
    </>
  )
}

import type { JSX } from "react"
import Image from "next/image"

interface ProductsProps {
  products: {
    image: string
    altImage: string
    name: string
    description: string
    size: string
    color: string
    quantity: number
    price: number
  }[]
}

export default function Products({
  products = [],
}: Readonly<ProductsProps>): JSX.Element {
  return (
    <div className="grid-col-1 grid gap-4 md:grid-cols-2 lg:grid-cols-3">
      {products.map((product) => (
        <article key={product.name} className="flex w-fit gap-4 bg-background">
          <Image
            src={product.image}
            alt={product.altImage}
            className="aspect-square rounded-lg"
            width={80}
            height={80}
          />

          <div className="flex flex-col justify-between py-1">
            <div>
              <small className="text-xs text-foreground/80 sm:text-sm">
                {product.name}
              </small>
              <p className="text-sm font-medium leading-none sm:text-base">
                {product.description}
              </p>
            </div>
            <div className="flex items-center gap-2 text-xs text-foreground/80">
              <small>Size: {product.size}</small>•
              <small>Color: {product.color}</small>
            </div>
          </div>

          <p className="flex h-fit items-center gap-1 py-1 text-sm font-medium text-foreground/80">
            {product.quantity}x ${product.price.toLocaleString()}
          </p>
        </article>
      ))}
    </div>
  )
}
